jQuery(document).ready(function($){$("#license-activate").on("click",function(){var button=$(this);isc_disable_license_buttons(true);var query={action:"isc-activate-license",license:$(this).parents("td").find("#license-key").val(),security:$("#isc-licenses-ajax-referrer").val()};$('<span class="spinner"></span>').insertAfter(button).show();$.post(ajaxurl,query,function(r){$("span.spinner").remove();var parent=button.parents("td");if(r==="1"){parent.find("#license-activate-error").hide();parent.find("#license-deactivate").show();button.fadeOut();parent.find("input").prop("readonly","readonly")}else{parent.find("#license-activate-error").show().html(r)}isc_disable_license_buttons(false)})});$("#license-deactivate").on("click",function(){var button=$(this);isc_disable_license_buttons(true);var query={action:"isc-deactivate-license",security:$("#isc-licenses-ajax-referrer").val()};$('<span class="spinner"></span>').insertAfter(button).show();$.post(ajaxurl,query,function(r){$("span.spinner").remove();if(r==="1"){button.siblings("#license-activate-error").hide();button.siblings("#license-activate").show();button.siblings("input").prop("readonly",false);button.fadeOut()}else{button.siblings("#license-activate-error").show().html(r)}isc_disable_license_buttons(false)})});function isc_disable_license_buttons(disable=true){var buttons=$("button#license-activate, button#license-deactivate");if(disable){buttons.attr("disabled","disabled")}else{buttons.removeAttr("disabled")}}});